        },
        duration: true,      // –í—Ä–µ–º—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è
        diff: true,          // –ü–æ–∫–∞–∑—ã–≤–∞—Ç—å –ø–æ–ª–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
        showFullState: true, // –ü–æ–∫–∞–∑—ã–≤–∞—Ç—å –ø–æ–ª–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
        // –ö–∞—Å—Ç–æ–º–Ω—ã–µ –ø–µ—Ä–µ–≤–æ–¥—ã –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å–∞
        translations: {
            action: '',
            changesCount: '',
            diff: '',
            duration: '',
