});

import { IStoragePlugin, StoragePluginModule } from 'synapse-storage/core';

// –°–æ–∑–¥–∞–µ–º –º–æ–¥—É–ª—å –ø–ª–∞–≥–∏–Ω–æ–≤
const plugins = new StoragePluginModule(
  undefined,      // –†–æ–¥–∏—Ç–µ–ª—å—Å–∫–∏–π –º–æ–¥—É–ª—å –ø–ª–∞–≥–∏–Ω–æ–≤ (–æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ)
  console,        // –õ–æ–≥–≥–µ—Ä
